const WebSocket = require('ws');

// Create a WebSocket server
const server = new WebSocket.Server({ port: 81 });

// Keep track of connected clients
const clients = new Set();

server.on('connection', (socket) => {
    console.log('A new client connected.');
    clients.add(socket); // Add the new client to the set

    // Send a welcome message to the client
    socket.send('Welcome to the WebSocket server!');

    // Listen for messages from clients
    socket.on('message', (message) => {
        console.log(`Received message: ${message}`);
        
        // Broadcast the received message to all other clients
        clients.forEach((client) => {
            if (client !== socket && client.readyState === WebSocket.OPEN) {
                client.send(`Message from another client: ${message}`);
            }
        });
    });

    // Handle client disconnection
    socket.on('close', () => {
        console.log('A client disconnected.');
        clients.delete(socket); // Remove the client from the set
    });
});

console.log('WebSocket server is running on ws://localhost:81');
